import React, {useRef} from 'react';
import { useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import {firestore} from "../firebase"
import {addDoc, collection} from "@firebase/firestore"

function Body() {

    const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0()
    const [name, setName] = useState();
    const [players, setPlayers] = useState([]);

    const messageRef = useRef();
    const ref = collection(firestore, "Users");

    const handleConfirm = async(e) => {
        e.preventDefault();
        setName(e.target.value)
        console.log(messageRef.current.value);
        let data = {
            name: messageRef.current.value
        }
        try {
            addDoc(ref, data);
        } catch (e) {
            console.log(e);
        }
    }

    const handleUserInput = async(e) => {
        e.preventDefault();
        console.log(name);
    }

    const handleNameChange = async (e) => {
        setName(e.target.value)
    }

    const handlePlayersChange = async (e) => {
        setName(e.target.value)
    }

    return(
        <>
        <div className="buttons-div">
            { !isAuthenticated ?
                <button id="login-button" onClick={() => loginWithRedirect()}>
                    Log in
                </button> : 
                <button id="logout-button" onClick={() => logout()}>
                    Log Out
                </button>
            }
        </div>
        { isAuthenticated ? 
            <div className="user-data">
                <form onSubmit={handleConfirm}>
                    <label>Type something:</label>
                    <input type="text" ref={messageRef} />
                    <button type="submit">Confirm</button>
                </form>
            </div> : "" }

            {
                isAuthenticated ?
                    <div className="logged-user-input">
                        <form onSubmit={handleUserInput}>
                            <div className='form-div'>
                                <label>Naziv natjecanja</label>
                                <input type="text" onChange={handleNameChange}></input>
                            </div>
                            <div className='form-div'>
                                <label>Imena natjecatelja (svaki u novi redak) </label>
                                <textarea type="text" id="players-input" onChange={handlePlayersChange}></textarea>
                            </div>
                            <button type="submit">Unesi</button>
                        </form>
                    </div>
                : ""
            }
        </>
    )

}

export default Body;
