import React, {useEffect} from 'react';
import { useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import {firestore} from "./firebase"
import {addDoc, getDocs, collection} from "@firebase/firestore"

function Tournament() {

    const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0();

    const ref = collection(firestore, "natjecanja");
    const [currTour, setCurrTour] = useState({});
    const [firstTeam, setFirstTeam] = useState("");
    const [secondTeam, setSecondTeam] = useState("");
    const [firstPoints, setFirstPoints] = useState();
    const [secondPoints, setSecondPoints] = useState();
    const [resultError, setResultError] = useState(false);
    const [teamsError, setTeamsError] = useState(false);
    const [gameFound, setGameFound] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const querySnapshot = await getDocs(ref);
                const documents = querySnapshot.docs.map((doc) => ({...doc.data(), id:doc.id }));
                for (let i = 0; i < documents.length; ++i) {
                    if (documents[i].kreator === localStorage.getItem(1)) {
                        let obj = {
                            id: documents[i].id,
                            naziv: documents[i].naziv,
                            kreator: documents[i].kreator,
                            pobjeda: documents[i].pobjeda,
                            remi: documents[i].remi,
                            poraz: documents[i].poraz,
                            igre: documents[i].igre,
                            sudionici: documents[i].sudionici
                        }
                        setCurrTour(obj);
                    }
                }

            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    },[]);

    const handleFirstTeamChange = (e) => {
        setFirstTeam(e.target.value);
    }

    const handleSecondTeamChange = (e) => {
        setSecondTeam(e.target.value);
    }

    const handleFirstPointsChange = (e) => {
        setFirstPoints(e.target.value);
    }

    const handleSecondPointsChange = (e) => {
        setSecondPoints(e.target.value);
    }

    const handleResultInput = (e) => {
        if (isNaN(firstPoints) || isNaN(secondPoints)) {
            setResultError(true);
        } else {
            setResultError(false);
        }
        for (let i = 0; i < currTour.igre.length; ++i) {
            if (currTour.igre[i].split("-")[1] === firstTeam && currTour.igre[i].split("-")[2] === secondTeam) {
                setGameFound(true);
            } 
        }
        if (!gameFound) {
            setTeamsError(true);
        }
    }

    return(
    <>
        <div className='table-title'>
            <div>REZULTATI</div>
            <div>{currTour.naziv}</div>
        </div>
        <div className='container'>
        <div className='table-div'>
            <table>
                {currTour.naziv ?<> <thead>
                    <td id="round">Kolo</td><td>Prva momčad</td><td>Druga Momčad</td><td>Rezultat</td>
                </thead>

                <tr>
                    <td id="round">{currTour.igre[0].split("-")[0]}.</td><td>{currTour.igre[0].split("-")[1]}</td><td>{currTour.igre[0].split("-")[2]}</td><td>0:0</td>
                </tr>
                <tr>
                    <td id="round">{currTour.igre[1].split("-")[0]}.</td><td>{currTour.igre[1].split("-")[1]}</td><td>{currTour.igre[1].split("-")[2]}</td><td>0:0</td>
                </tr>
                <tr>
                    <td id="round">{currTour.igre[2].split("-")[0]}.</td><td>{currTour.igre[2].split("-")[1]}</td><td>{currTour.igre[2].split("-")[2]}</td><td>0:0</td>
                </tr>
                <tr>
                    <td id="round">{currTour.igre[3].split("-")[0]}.</td><td>{currTour.igre[3].split("-")[1]}</td><td>{currTour.igre[3].split("-")[2]}</td><td>0:0</td>
                </tr>
                <tr>
                    <td id="round">{currTour.igre[4].split("-")[0]}.</td><td>{currTour.igre[4].split("-")[1]}</td><td>{currTour.igre[4].split("-")[2]}</td><td>0:0</td>
                </tr>
                <tr>
                    <td id="round">{currTour.igre[5].split("-")[0]}.</td><td>{currTour.igre[5].split("-")[1]}</td><td>{currTour.igre[5].split("-")[2]}</td><td>0:0</td>
                </tr> </>: ""}
                {
                    currTour.naziv && currTour.sudionici.length > 4 ?
                        <>
                            <tr>
                                <td id="round">{currTour.igre[6].split("-")[0]}.</td><td>{currTour.igre[6].split("-")[1]}</td><td>{currTour.igre[6].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[7].split("-")[0]}.</td><td>{currTour.igre[7].split("-")[1]}</td><td>{currTour.igre[7].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[8].split("-")[0]}.</td><td>{currTour.igre[8].split("-")[1]}</td><td>{currTour.igre[8].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[9].split("-")[0]}.</td><td>{currTour.igre[9].split("-")[1]}</td><td>{currTour.igre[9].split("-")[2]}</td><td>0:0</td>
                            </tr>
                        </>
                    : ""
                }
                {
                    currTour.naziv && currTour.sudionici.length > 5 ?
                        <>
                            <tr>
                                <td id="round">{currTour.igre[10].split("-")[0]}.</td><td>{currTour.igre[10].split("-")[1]}</td><td>{currTour.igre[10].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[11].split("-")[0]}.</td><td>{currTour.igre[11].split("-")[1]}</td><td>{currTour.igre[11].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[12].split("-")[0]}.</td><td>{currTour.igre[12].split("-")[1]}</td><td>{currTour.igre[12].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[13].split("-")[0]}.</td><td>{currTour.igre[13].split("-")[1]}</td><td>{currTour.igre[13].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[14].split("-")[0]}.</td><td>{currTour.igre[14].split("-")[1]}</td><td>{currTour.igre[14].split("-")[2]}</td><td>0:0</td>
                            </tr>
                        </>
                    : ""
                }
                {
                    currTour.naziv && currTour.sudionici.length > 6 ?
                        <>
                            <tr>
                                <td id="round">{currTour.igre[15].split("-")[0]}.</td><td>{currTour.igre[15].split("-")[1]}</td><td>{currTour.igre[15].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
                                <td id="round">{currTour.igre[16].split("-")[0]}.</td><td>{currTour.igre[16].split("-")[1]}</td><td>{currTour.igre[16].split("-")[2]}</td><td>0:0</td>
                            </tr>
                            <tr>
