            games.push("3-" + players[3]+"-"+players[1]+"-?-?");
            games.push("3-" + players[4]+"-"+players[0]+"-?-?");
            games.push("3-" + players[5]+"-"+players[6]+"-?-?");
            games.push("4-" + players[3]+"-"+players[7]+"-?-?");
            games.push("4-" + players[4]+"-"+players[2]+"-?-?");
            games.push("4-" + players[5]+"-"+players[1]+"-?-?");
